function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [userQuestion, setUserQuestion] = useState('');
  const [result, setResult] = useState('');
  const [id, setID] = useState('');

  const handleQuestionChange = (event) => {
    setUserQuestion(event.target.value);
  }

  const handleFileChange = (event) => {
    setPdfFile(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

  const formData = new FormData();

  if (pdfFile) {
    formData.append("file", pdfFile);
  }
  if(userQuestion) {
    formData.append('question', userQuestion);
  }

  fetch("http://127.0.0.1:5000/process_pdf", {
    method: "POST",
    body: formData,
  })
    .then((response) => {
      console.log("PDF file sent successfully");
      console.log(response)
      return response.json();
    })
    .then((data) => {
      setResult(data?.ans|| 'No answer found'); //name the same as python result
      console.log(data?.ans)
      setID(data.id);
    })
    .catch((error) => {
      console.error("Error", error);
    });
  };

  //fetch("http://127.0.0.1:5000/process_pdf/"+, {

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="submit-button">
        <label className="q-box-container" htmlFor="question">
          Question:
        </label>
        <input
          className="q-box"
          id="question"
          type="text"
          value={userQuestion}
          onChange={handleQuestionChange}
          placeholder="Ask your question here"
        />
        <label className="csv-block" htmlFor="file">
          Upload PDF file:
        </label>
        <input
          type="file"
          id="file"
          name="file"
          accept=".pdf"
          onChange={handleFileChange}
          className="mb-3"
        />
        <button
          className="submit-box"
          type="submit"
          disabled={!pdfFile || !userQuestion}
        >
          Submit
        </button>
      </form>
      <p className="result-box">Result: {result}</p>
    </div>
  );
}

export default App;
